mapper.update:
UPDATE
  $[SRC_TABLE_COC_LABEL_STATUS]
SET
  DATA_STATUS    =$[STATUS_LABEL_CNT_FAIL] ,
  EXCEPTION_DESC = #{errMsg}
WHERE
  DATA_STATUS  =$[STATUS_LABEL_CNT_RUNNING]
AND DATA_DATE  =#{date}
AND DATA_BATCH =#{batchNo}
AND LABEL_ID   =#{labelId}

 UPDATE
  $[SRC_TABLE_DST_TABLE_STATUS]
SET
  STATUS_ID     =#{newStatus} ,
  DATA_BATCH    =#{newBatchNo},
  pid           =#{pid},
  EXCEPTION_DESC=#{desc}
WHERE
  STATUS_ID    =#{oldStatus}
AND DATA_DATE  =#{date}
AND TABLE_TYPE =#{tableType}

	AND TABLE_ID =#{tableId}



		 AND DATA_BATCH = #{batchNo} 
		AND DATA_BATCH  <>  #{batchNo}

mapper.select:
SELECT
  COUNT(1)
FROM
  $[SRC_TABLE_DST_TABLE_STATUS]
WHERE
  TABLE_ID     =#{tableId}
AND STATUS_ID  =#{statusId}
AND TABLE_TYPE =#{tableType}
AND DATA_DATE  =#{date}


		 AND DATA_BATCH = #{batchNo} 
		AND DATA_BATCH  <>  #{batchNo}

mapper.update:
UPDATE
  $[SRC_TABLE_COC_LABEL_STATUS]
SET
  DATA_STATUS =$[STATUS_LABEL_CNT_SEC],
  data_batch  =#{batchNo}
WHERE
  LABEL_ID     IN (${labelId})
AND DATA_DATE  =#{date}
AND data_batch =#{batchNo}
AND DATA_STATUS=$[STATUS_LABEL_CNT_RUNNING]

UPDATE
  $[SRC_TABLE_DST_TABLE_STATUS]
SET
  STATUS_ID  =$[STATUS_LABEL_CNT_DST_SEC],
  DATA_BATCH =#{batchNo}
WHERE
  TABLE_ID     =#{tableId}
AND STATUS_ID  <> $[STATUS_LABEL_DST_SEC]
AND DATA_DATE  =#{date}
AND TABLE_TYPE =$[TABLE_TYPE_LABEL_CNT_TABLE]
